-- 创建数据库并设置字符集为 utf8
CREATE DATABASE IF NOT EXISTS memorized CHARACTER SET utf8 COLLATE utf8_general_ci;
USE memorized;

-- 创建表并设置字符集为 utf8
CREATE TABLE words (
    id INT NOT NULL,
    word VARCHAR(255) NOT NULL,
    bookId VARCHAR(50) NOT NULL,
    tranCn VARCHAR(255) NOT NULL,
    pos VARCHAR(50) NOT NULL,
    usphone VARCHAR(255) NOT NULL,
    ukphone VARCHAR(255) NOT NULL,
    sContent TEXT NOT NULL,
    sCn TEXT NOT NULL,
    PRIMARY KEY (id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;


